mapper.select:
SELECT MENUID, SIKNO, MENULIST, MENUDATE, MENUFILESRC
FROM
MENU WHERE MENUID=#{param1}

SELECT MENUID, SIKNO, MENULIST, MENUDATE, MENUFILESRC
FROM
MENU WHERE MENUID=#{param1}

mapper.update:
UPDATE MENU SET MENULIST=#{PARAM2},
MENUDATE=#{PARAM3},
MENUFILESRC=#{PARAM4} WHERE MENUID=#{PARAM1}
